import React from 'react';
import { motion } from 'framer-motion';

const team = [
  { name: 'Jacob', role: 'SEO Specialist', avatar: '/images/bot-avatar.png' },
  { name: 'Maya', role: 'Client Success', avatar: '/images/bot-avatar.png' },
  { name: 'Alex', role: 'Support', avatar: '/images/bot-avatar.png' },
];

const WhyRankly360 = () => {
  return (
    <section id="why-us" className="relative py-24 bg-gradient-to-b from-[#0f0f1a] to-[#09090f] overflow-hidden">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 flex flex-col md:flex-row items-center gap-12">
        {/* Left: Headline, subheadline, how we work */}
        <div className="flex-1 min-w-[320px] max-w-xl">
          <motion.h2
            initial={{ opacity: 0, y: 40 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
            className="text-4xl md:text-5xl font-extrabold text-white mb-6 tracking-tight"
          >
            What It's Like Working With Us
          </motion.h2>
          <motion.p
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
            viewport={{ once: true }}
            className="text-xl text-blue-100 mb-8 max-w-lg"
          >
            We’re a small team that likes to keep things simple and honest. We don’t have a call center or sales scripts—just real people who care about helping your business get found.
          </motion.p>
          <div className="mb-8">
            <h3 className="text-lg font-bold text-white mb-2">How We Work</h3>
            <ul className="list-disc list-inside text-blue-100 text-base space-y-2">
              <li>We listen to your story and goals before suggesting anything.</li>
              <li>We explain what we’re doing in plain English, not jargon.</li>
              <li>If something isn’t working, we’ll tell you and try something else.</li>
              <li>You can call or text us directly—no ticket system.</li>
              <li>We don’t do contracts. If you want to leave, you can, no hard feelings.</li>
            </ul>
          </div>
          <div className="bg-[#181c2a]/80 border border-[#3abef9]/20 rounded-xl px-6 py-4 shadow-md mb-8">
            <p className="text-white text-base italic mb-2">
              “I was honestly skeptical at first, but these guys were patient and explained everything. I never felt pressured. After a few months, I started getting more calls. It’s just nice to work with people who treat you like a person.”
            </p>
            <div className="text-blue-100 text-sm">— Sam L., Local Shop Owner</div>
          </div>
        </div>
        {/* Right: Meet your team */}
        <div className="flex-1 flex flex-col items-center min-w-[320px]">
          <h3 className="text-xl font-bold text-white mb-4">Meet Your Team</h3>
          <div className="flex gap-6 mb-8">
            {team.map((member, i) => (
              <div key={i} className="flex flex-col items-center">
                <img src={member.avatar} alt={member.name} className="w-20 h-20 rounded-full border-4 border-[#3abef9] mb-2 bg-white object-cover" />
                <div className="text-white font-semibold">{member.name}</div>
                <div className="text-blue-100 text-xs">{member.role}</div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default WhyRankly360;
