import React, { useRef, useEffect } from 'react';
import { motion, useAnimation } from 'framer-motion';
import { Star, Quote } from 'lucide-react';

const testimonials = [
  {
    name: "Emily Carter",
    business: "Boutique Owner",
    image: "https://randomuser.me/api/portraits/women/44.jpg",
    quote: "Rankly360 helped my shop show up higher in Google search and on Maps within just a few months. Their team handled everything and I’ve seen a big increase in calls and website visits. Highly recommend!"
  },
  {
    name: "Marcus Lee",
    business: "Basketball Trainer",
    image: "https://randomuser.me/api/portraits/men/32.jpg",
    quote: "Rankly360 took my basketball training business to the next level. I started getting way more client inquiries through my website. Super professional and easy to work with."
  },
  {
    name: "Sofia Martinez",
    business: "Dental Clinic Manager",
    image: "https://randomuser.me/api/portraits/women/68.jpg",
    quote: "We went from barely being found to dominating our local market. Our patient bookings are up and our Google profile looks amazing."
  },
  {
    name: "James O'Connor",
    business: "Auto Repair Shop Owner",
    image: "https://randomuser.me/api/portraits/men/65.jpg",
    quote: "I was skeptical at first, but the results speak for themselves. We're now the first auto shop people see when they search locally. Business has never been better."
  },
  {
    name: "Priya Desai",
    business: "Yoga Studio Director",
    image: "https://randomuser.me/api/portraits/women/12.jpg",
    quote: "The Rankly360 team made everything easy. We’re getting more calls and new students every week. I love how transparent and responsive they are."
  }
];

const CARD_WIDTH = 340; // px, including margin
const VISIBLE_CARDS = 3; // 3 on desktop, 1 on mobile (handled by CSS)
const ANIMATION_DURATION = 20; // seconds for one full loop (was 8)

const ClientResults = () => {
  const controls = useAnimation();
  const stripRef = useRef(null);

  // Duplicate testimonials for seamless looping
  const loopTestimonials = [...testimonials, ...testimonials];

  useEffect(() => {
    let isMounted = true;
    const animate = async () => {
      while (isMounted) {
        await controls.start({
          x: -CARD_WIDTH * testimonials.length,
          transition: { duration: ANIMATION_DURATION, ease: 'linear' }
        });
        if (isMounted) {
          controls.set({ x: 0 });
        }
      }
    };
    animate();
    return () => { isMounted = false; };
    // eslint-disable-next-line
  }, []);

  return (
    <section className="py-20 bg-white">
      {/* Before & After Comparison */}
      <div className="max-w-5xl mx-auto px-4 mb-16">
        <h2 className="text-4xl md:text-5xl font-bold text-primary-900 text-center mb-4">Before & After: Real Local SEO Results</h2>
        <p className="text-lg text-gray-600 text-center mb-10 max-w-2xl mx-auto">
          Visual proof: Real Google Map Pack rankings before and after our local SEO campaigns.
        </p>
        <div className="flex flex-col md:flex-row gap-8 items-center justify-center">
          <div className="flex-1 text-center">
            <div className="mb-2 font-semibold text-gray-700">Before</div>
            <img
              src="/images/eBHbpbY.gif"
              alt="Local SEO Map Rankings Before"
              className="rounded-2xl shadow-lg border border-gray-100 w-full max-w-md mx-auto"
              style={{objectFit: 'contain'}}
            />
          </div>
          <div className="flex-1 text-center">
            <div className="mb-2 font-semibold text-gray-700">After</div>
            <img
              src="/images/XMgbFwE.gif"
              alt="Local SEO Map Rankings After"
              className="rounded-2xl shadow-lg border border-gray-100 w-full max-w-md mx-auto"
              style={{objectFit: 'contain'}}
            />
          </div>
        </div>
      </div>
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        {/* Section Header */}
        <motion.div 
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl md:text-5xl font-bold text-primary-900 mb-6">
            Proven Results
          </h2>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            Real clients, real rankings, real business growth
          </p>
        </motion.div>

        {/* Perpetual Strip Carousel */}
        <div className="overflow-hidden w-full max-w-5xl mx-auto">
          <motion.div
            ref={stripRef}
            animate={controls}
            className="flex"
            style={{ width: `${CARD_WIDTH * loopTestimonials.length}px` }}
          >
            {loopTestimonials.map((t, idx) => (
              <div
                key={idx}
                className="bg-gradient-to-br from-primary-50 to-orange-50 rounded-2xl p-8 md:p-12 m-4 flex-shrink-0 shadow-lg relative"
                style={{ width: CARD_WIDTH - 32 }}
              >
                <div className="absolute top-8 right-8">
                  <Quote className="w-12 h-12 text-orange-500/20" />
                </div>
                <div className="flex flex-col items-center text-center gap-6">
                  <img
                    src={t.image}
                    alt={t.name}
                    className="w-20 h-20 rounded-full mx-auto mb-4 object-cover"
                  />
                  <h3 className="font-bold text-gray-900 text-lg">{t.name}</h3>
                  <p className="text-gray-600 mb-2">{t.business}</p>
                  <blockquote className="text-xl text-gray-700 leading-relaxed italic">
                    "{t.quote}"
                  </blockquote>
                  <div className="flex items-center gap-1 justify-center mt-2">
                    {[...Array(5)].map((_, i) => (
                      <Star key={i} className="w-5 h-5 text-orange-500 fill-current" />
                    ))}
                  </div>
                </div>
              </div>
            ))}
          </motion.div>
        </div>

        {/* Stats Section */}
        <motion.div 
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="mt-20"
        >
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-4xl mx-auto">
            <div className="text-center">
              <div className="text-4xl font-bold text-primary-900 mb-2">500+</div>
              <div className="text-gray-600">Local Rankings Achieved</div>
            </div>
            <div className="text-center">
              <div className="text-4xl font-bold text-primary-900 mb-2">150+</div>
              <div className="text-gray-600">Happy Clients</div>
            </div>
            <div className="text-center">
              <div className="text-4xl font-bold text-primary-900 mb-2">30</div>
              <div className="text-gray-600">Day Average to Results</div>
            </div>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default ClientResults;
