import React from 'react';
import { useNavigate } from 'react-router-dom';

const features = [
  '100+ high-quality local citations',
  'Manual submissions to top directories',
  'NAP consistency checks',
  'Detailed reporting',
  'Safe, white-hat methods',
  'Guaranteed accuracy and transparency'
];

export default function LocalCitations() {
  const navigate = useNavigate();
  return (
    <section className="min-h-screen bg-primary-950 text-white py-16 px-4 flex flex-col items-center justify-center">
      <div className="max-w-2xl w-full bg-primary-900 rounded-3xl shadow-2xl p-8 md:p-12">
        <h1 className="text-4xl md:text-5xl font-bold mb-4 text-electric-blue">Local Citations for Local SEO</h1>
        <p className="text-lg text-gray-200 mb-6">Build trust and authority with 100+ high-quality local citations. Our manual, transparent process ensures your business is listed accurately and safely across the web.</p>
        <h2 className="text-2xl font-semibold mb-2 text-orange-400">What’s Included:</h2>
        <ul className="mb-6 space-y-2">
          {features.map((f, i) => (
            <li key={i} className="flex items-center gap-2">
              <span className="inline-block w-2 h-2 bg-electric-blue rounded-full"></span>
              <span>{f}</span>
            </li>
          ))}
        </ul>
        <h2 className="text-xl font-semibold mb-2 text-orange-400">Why Choose Our Local Citations?</h2>
        <ul className="mb-6 space-y-2 text-gray-200">
          <li>• Boosts local trust and Google Map Pack rankings</li>
          <li>• 100% manual, safe, and white-hat</li>
          <li>• Full reporting and guaranteed accuracy</li>
        </ul>
        <button onClick={() => navigate(-1)} className="mt-6 px-6 py-3 rounded-xl bg-electric-blue text-primary-950 font-bold hover:bg-orange-400 transition">Back to Packages</button>
      </div>
    </section>
  );
}
