import React from 'react';
import { motion } from 'framer-motion';
import { Star, ShieldCheck, Users, TrendingUp } from 'lucide-react';
import { useState, useEffect } from 'react';

const clientWins = [
  { city: 'Miami', type: 'Dentist', stat: '+85% calls', icon: 'ü¶∑' },
  { city: 'Austin', type: 'Bakery', stat: '+120% GMB views', icon: 'ü•ê' },
  { city: 'NYC', type: 'Law Firm', stat: 'Top 3 in Maps', icon: '‚öñÔ∏è' },
  { city: 'Houston', type: 'Plumber', stat: 'Top 5 in Maps', icon: 'üîß' },
  { city: 'Chicago', type: 'Salon', stat: '2x more bookings', icon: 'üíá‚Äç‚ôÄÔ∏è' },
  { city: 'Atlanta', type: 'Chiropractor', stat: '+95% new leads', icon: 'üíÜ‚Äç‚ôÇÔ∏è' },
  { city: 'Dallas', type: 'Restaurant', stat: 'Top 3 for "best tacos"', icon: 'üåÆ' },
  { city: 'Toronto', type: 'Landscaper', stat: '+75% website visits', icon: 'üå≥' },
];

function AnimatedStatBar() {
  const [count, setCount] = useState(0);
  useEffect(() => {
    let start = 0;
    const end = 150;
    if (count < end) {
      const timer = setInterval(() => {
        setCount((prev) => {
          if (prev < end) return prev + 2;
          clearInterval(timer);
          return end;
        });
      }, 20);
      return () => clearInterval(timer);
    }
  }, [count]);
  return (
    <div className="w-full max-w-md mx-auto mt-8 mb-2">
      <div className="text-center text-lg text-blue-100 mb-2 font-semibold">Over <span className="text-[#3abef9] text-2xl font-bold">{count}+</span> Local Businesses Helped</div>
      <div className="relative h-4 bg-[#181c2a] rounded-full overflow-hidden shadow-inner">
        <div className="absolute left-0 top-0 h-full bg-gradient-to-r from-[#3abef9] to-[#00ffff] rounded-full shadow-lg" style={{ width: `${Math.min(count / 1.5, 100)}%` }} />
      </div>
    </div>
  );
}

function ClientSuccessWall() {
  return (
    <div className="w-full flex flex-col items-center">
      <h3 className="text-xl font-bold text-white mb-2 mt-2">Our Local SEO Wins</h3>
      <p className="text-blue-100 text-sm mb-6">Real results for real businesses‚Äîacross industries and cities.</p>
      <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 md:grid-cols-3 lg:grid-cols-4 w-full max-w-2xl mb-6">
        {clientWins.map((win, i) => (
          <div
            key={i}
            className="bg-[#181c2a]/80 border border-[#3abef9]/20 rounded-2xl p-4 flex flex-col items-center shadow-md hover:shadow-xl transition-all duration-300 group"
            style={{ animation: `fadeInUp 0.5s ${i * 0.08}s both` }}
          >
            <div className="text-3xl mb-2 animate-float2">{win.icon}</div>
            <div className="text-white font-semibold text-base mb-1">{win.type} <span className="text-blue-200">in {win.city}</span></div>
            <div className="text-[#3abef9] font-bold text-lg">{win.stat}</div>
          </div>
        ))}
      </div>
    </div>
  );
}

const trustBadges = [
  { icon: <ShieldCheck className="w-6 h-6 text-blue-400" />, label: 'Google Partner' },
