import React from 'react';
import { motion } from 'framer-motion';

const testimonials = [
  {
    name: 'Sarah M.',
    avatar: '/images/avatar1.png',
    review: 'Rankly360 got us into the Google Map Pack in 6 weeks. Our calls doubled! Highly recommend their team.'
  },
  {
    name: 'Mike D.',
    avatar: '/images/avatar2.png',
    review: 'The Map Embeds and GBP Optimization made a real difference. We saw results fast.'
  },
  {
    name: 'Jenna R.',
    avatar: '/images/avatar3.png',
    review: 'Professional, transparent, and effective. The best local SEO agency we’ve worked with.'
  }
];

const Testimonials = () => (
  <section id="testimonials" className="py-16 bg-gray-50 dark:bg-[#1e293b]">
    <div className="max-w-5xl mx-auto px-4">
      <h2 className="text-3xl font-bold text-center mb-10">What Our Clients Say</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
        {testimonials.map((t, i) => (
          <motion.div
            key={i}
            initial={{ opacity: 0, y: 40 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: i * 0.15 }}
            viewport={{ once: true }}
            className="bg-white dark:bg-[#0F172A] rounded-xl shadow-lg p-6 flex flex-col items-center text-center border border-gray-100 dark:border-gray-700"
          >
            <img src={t.avatar} alt={t.name} className="w-16 h-16 rounded-full mb-4 border-2 border-orange-500 bg-white" />
            <p className="text-lg mb-3 font-medium">“{t.review}”</p>
            <span className="font-semibold text-orange-500">{t.name}</span>
          </motion.div>
        ))}
      </div>
    </div>
  </section>
);

export default Testimonials;
