import React from 'react';
import { motion } from 'framer-motion';
import { Check, Star, ArrowRight } from 'lucide-react';

const Pricing = () => {
  const plans = [
    {
      name: 'Starter',
      price: '$297',
      period: '/month',
      description: 'Perfect for small businesses just getting started with local SEO.',
      features: [
        'Google Business Profile Setup & Optimization',
        'Basic Review Generation Strategy',
        'Monthly Ranking Reports',
        'Email Support',
        'Local Keyword Research (5 keywords)',
        'Basic Citation Building'
      ],
      popular: false,
      cta: 'Get Started',
      color: 'from-gray-500 to-gray-600'
    },
    {
      name: 'Growth',
      price: '$497',
      period: '/month',
      description: 'Our most popular plan for businesses serious about local growth.',
      features: [
        'Everything in Starter, plus:',
        'Advanced Review Management',
        'Citation Cleanup & Building',
        'Local Backlink Building',
        'Competitor Analysis',
        'Weekly Progress Calls',
        'Priority Support',
        'Custom Local SEO Strategy'
      ],
      popular: true,
      cta: 'Get Started',
      color: 'from-primary-500 to-orange-500'
    },
    {
      name: 'Dominate',
      price: '$997',
      period: '/month',
      description: 'Complete local SEO domination for established businesses.',
      features: [
        'Everything in Growth, plus:',
        'Google Ads Management',
        'Monthly Strategy Calls',
        'Content Creation & Optimization',
        'Advanced Analytics Dashboard',
        'White-label Reporting',
        'Dedicated Account Manager',
        'Custom Landing Pages'
      ],
      popular: false,
      cta: 'Get Started',
      color: 'from-purple-500 to-pink-500'
    }
  ];

  return (
    <section id="pricing" className="section-padding bg-gray-50">
      <div className="container-custom">
        {/* Section Header */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl lg:text-5xl font-bold text-gray-900 mb-6 tracking-tight">
            Simple Pricing for Local Growth
          </h2>
          <p className="text-xl lg:text-2xl text-gray-600 max-w-4xl mx-auto leading-relaxed font-light">
            Choose the plan that fits your business needs. All plans include our proven local SEO methodology.
          </p>
        </motion.div>

        {/* Pricing Cards */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 max-w-6xl mx-auto">
          {plans.map((plan, index) => (
            <motion.div
              key={plan.name}
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: index * 0.1 }}
              viewport={{ once: true }}
              className={`relative ${plan.popular ? 'lg:scale-105' : ''}`}
            >
              {/* Popular Badge */}
              {plan.popular && (
                <div className="absolute -top-4 left-1/2 transform -translate-x-1/2 z-10">
                  <div className="bg-gradient-to-r from-orange-500 to-red-500 text-white px-6 py-2 rounded-full text-sm font-semibold flex items-center space-x-1">
                    <Star className="w-4 h-4" />
                    <span>Most Popular</span>
                  </div>
                </div>
              )}

              <div className={`premium-card p-8 h-full relative overflow-hidden ${plan.popular ? 'ring-2 ring-orange-500 shadow-3xl' : ''}`}>
                {/* Plan Header */}
                <div className="text-center mb-8">
                  <h3 className="text-2xl font-bold text-gray-900 mb-2 tracking-tight">
                    {plan.name}
                  </h3>
                  <p className="text-gray-600 mb-6 leading-relaxed">
                    {plan.description}
                  </p>

                  {/* Price */}
                  <div className="mb-6">
                    <div className="flex items-baseline justify-center">
                      <span className="text-6xl font-bold text-gradient">
                        {plan.price}
                      </span>
                      <span className="text-gray-500 ml-2 text-lg">
                        {plan.period}
                      </span>
                    </div>
                  </div>
                </div>

                {/* Features */}
                <ul className="space-y-4 mb-8">
                  {plan.features.map((feature, featureIndex) => (
                    <li key={featureIndex} className="flex items-start">
                      <Check className="w-5 h-5 text-green-500 mr-3 mt-0.5 flex-shrink-0" />
                      <span className="text-gray-700">{feature}</span>
                    </li>
                  ))}
                </ul>

                {/* CTA Button */}
                <motion.button
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  className={`w-full py-3 px-6 rounded-lg font-semibold text-white bg-gradient-to-r ${plan.color} hover:shadow-lg transition-all duration-300 flex items-center justify-center space-x-2`}
                >
                  <span>{plan.cta}</span>
                  <ArrowRight className="w-4 h-4" />
                </motion.button>
              </div>
            </motion.div>
          ))}
        </div>

        {/* Additional Info */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="text-center mt-16"
        >
          <div className="bg-white rounded-2xl p-8 shadow-lg border border-gray-100 max-w-4xl mx-auto">
            <h3 className="text-2xl font-bold text-gray-900 mb-4">
              All Plans Include
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="text-center">
                <div className="w-12 h-12 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4">
                  <Check className="w-6 h-6 text-green-600" />
                </div>
                <h4 className="font-semibold text-gray-900 mb-2">30-Day Money Back</h4>
                <p className="text-gray-600 text-sm">Not satisfied? Get a full refund within 30 days.</p>
              </div>
              <div className="text-center">
                <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4">
                  <Check className="w-6 h-6 text-blue-600" />
                </div>
                <h4 className="font-semibold text-gray-900 mb-2">No Setup Fees</h4>
                <p className="text-gray-600 text-sm">Start immediately with no hidden costs or setup fees.</p>
              </div>
              <div className="text-center">
                <div className="w-12 h-12 bg-orange-100 rounded-full flex items-center justify-center mx-auto mb-4">
                  <Check className="w-6 h-6 text-orange-600" />
                </div>
                <h4 className="font-semibold text-gray-900 mb-2">Cancel Anytime</h4>
                <p className="text-gray-600 text-sm">No long-term contracts. Cancel whenever you want.</p>
              </div>
            </div>
          </div>
        </motion.div>

        {/* FAQ CTA */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="text-center mt-12"
        >
          <p className="text-gray-600 mb-4">
            Questions about our pricing or services?
          </p>
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            className="btn-secondary"
          >
            Schedule a Free Consultation
          </motion.button>
        </motion.div>
      </div>
    </section>
  );
};

export default Pricing;
