import React from 'react';
import { motion } from 'framer-motion';
import { 
  Award, 
  Users, 
  TrendingUp, 
  Shield, 
  Clock, 
  Zap,
  CheckCircle,
  Star,
  Target,
  BarChart3
} from 'lucide-react';

const WhyRankly360 = () => {
  const reasons = [
    {
      icon: Award,
      title: "Proven Track Record",
      description: "Over 500+ local businesses have achieved top 3 Google Map Pack rankings with our strategies. Our clients consistently see 200-500% increases in local leads.",
      color: "text-blue-600"
    },
    {
      icon: Users,
      title: "Expert Team",
      description: "Our team consists of certified Google Partners and local SEO specialists with 8+ years of experience in map pack domination strategies.",
      color: "text-orange-500"
    },
    {
      icon: TrendingUp,
      title: "Data-Driven Approach",
      description: "We use advanced analytics and proprietary tools to track every aspect of your local SEO performance, ensuring measurable results.",
      color: "text-green-600"
    },
    {
      icon: Shield,
      title: "White-Hat Methods Only",
      description: "All our strategies follow Google's guidelines and use legitimate, sustainable techniques that protect your business long-term.",
      color: "text-purple-600"
    },
    {
      icon: Clock,
      title: "Fast Results",
      description: "Most clients see significant improvements within 30-60 days, with some achieving top rankings in as little as 2 weeks.",
      color: "text-red-600"
    },
    {
      icon: Zap,
      title: "Cutting-Edge Technology",
      description: "We leverage the latest local SEO tools and techniques, including advanced map embed stacking and authority building strategies.",
      color: "text-indigo-600"
    }
  ];

  const stats = [
    {
      icon: Target,
      value: "500+",
      label: "Businesses Helped",
      description: "Local businesses that achieved top rankings"
    },
    {
      icon: Star,
      value: "98%",
      label: "Success Rate",
      description: "Of clients see significant improvements"
    },
    {
      icon: BarChart3,
      value: "300%",
      label: "Average Lead Increase",
      description: "Typical improvement in local leads"
    },
    {
      icon: CheckCircle,
      value: "4.9/5",
      label: "Client Rating",
      description: "Average satisfaction score"
    }
  ];

  return (
    <section id="why-us" className="relative py-20 bg-gradient-to-b from-[#0f0f1a] to-[#09090f] overflow-hidden">
      {/* Top gradient fade for smooth transition from previous section */}
      <div className="absolute top-0 left-0 w-full h-24 bg-gradient-to-t from-transparent to-[#0f0f1a] pointer-events-none z-20" />
      {/* Space background effects */}
      <div className="absolute inset-0 pointer-events-none select-none z-0">
        <div className="absolute top-24 left-1/3 w-1 h-1 bg-[#3abef9]/40 rounded-full blur-[1px]" />
        <div className="absolute top-48 right-1/4 w-0.5 h-0.5 bg-white/30 rounded-full blur-[0.8px]" />
        <div className="absolute bottom-36 left-1/5 w-1.5 h-1.5 bg-[#00ffff]/50 rounded-full blur-[1.2px]" />
        <div className="absolute bottom-24 right-1/5 w-0.8 h-0.8 bg-[#3abef9]/35 rounded-full blur-[1px]" />
        {/* Extra stars */}
        <div className="absolute top-1/2 left-1/3 w-0.5 h-0.5 bg-white/20 rounded-full blur-[1px]" />
        <div className="absolute top-1/3 right-1/4 w-1 h-1 bg-[#00ffff]/30 rounded-full blur-[1.2px]" />
      </div>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl md:text-5xl font-bold text-white mb-6">
            Why Choose Rankly360?
          </h2>
          <p className="text-xl text-gray-300 max-w-3xl mx-auto">
            We're not just another SEO company. We're local business growth specialists who understand what it takes to dominate your local market.
          </p>
        </motion.div>

        {/* Stats Section */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
          viewport={{ once: true }}
          className="grid grid-cols-2 md:grid-cols-4 gap-8 mb-20"
        >
          {stats.map((stat, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, scale: 0.8 }}
              whileInView={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              viewport={{ once: true }}
              className="text-center"
            >
              <div className="bg-[#1a1a1a] rounded-2xl p-6 shadow-lg border border-[#3ABEF9]/20 hover:shadow-xl transition-all duration-300">
                <div className="inline-flex items-center justify-center w-16 h-16 bg-gradient-to-r from-[#3ABEF9] to-[#007BFF] rounded-full mb-4">
                  <stat.icon className="w-8 h-8 text-white" />
                </div>
                <div className="text-3xl font-bold text-white mb-2">
                  {stat.value}
                </div>
                <div className="text-sm font-semibold text-gray-300 mb-1">
                  {stat.label}
                </div>
                <div className="text-xs text-gray-400">
                  {stat.description}
                </div>
              </div>
            </motion.div>
          ))}
        </motion.div>

        {/* Reasons Grid */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.4 }}
          viewport={{ once: true }}
          className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8"
        >
          {reasons.map((reason, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              viewport={{ once: true }}
              className="group"
            >
              <div className="bg-[#1a1a1a] rounded-2xl p-8 shadow-lg border border-[#2a2a2a] hover:shadow-xl transition-all duration-300 h-full group-hover:-translate-y-2 group-hover:border-[#3ABEF9]/30">
                <div className={`inline-flex items-center justify-center w-14 h-14 bg-gradient-to-r from-[#1a1a1a] to-[#2a2a2a] rounded-xl mb-6 group-hover:scale-110 transition-transform duration-300 border border-[#3ABEF9]/20`}>
                  <reason.icon className={`w-7 h-7 ${reason.color}`} />
                </div>
                <h3 className="text-xl font-bold text-white mb-4">
                  {reason.title}
                </h3>
                <p className="text-gray-300 leading-relaxed">
                  {reason.description}
                </p>
              </div>
            </motion.div>
          ))}
        </motion.div>

        {/* CTA Section */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.6 }}
          viewport={{ once: true }}
          className="text-center mt-16"
        >
          <div className="bg-gradient-to-r from-[#1a1a1a] to-[#2a2a2a] rounded-3xl p-8 md:p-12 text-white max-w-4xl mx-auto border border-[#3ABEF9]/20">
            <h3 className="text-3xl font-bold mb-4">
              Ready to Dominate Your Local Market?
            </h3>
            <p className="text-xl text-gray-300 mb-8">
              Join hundreds of businesses that have transformed their local presence with Rankly360. Let's get you to the top of Google Maps.
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <button className="bg-gradient-to-r from-[#3ABEF9] to-[#007BFF] hover:from-[#007BFF] hover:to-[#0052CC] text-white font-semibold py-4 px-8 rounded-xl transition-all duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl">
                Get Started Today
              </button>
              <button className="bg-transparent border-2 border-[#3ABEF9] text-white font-semibold py-4 px-8 rounded-xl transition-all duration-300 hover:bg-[#3ABEF9] hover:text-white">
                View Our Results
              </button>
            </div>
          </div>
        </motion.div>
      </div>
      {/* Bottom gradient fade for smooth transition to next section */}
      <div className="absolute bottom-0 left-0 w-full h-24 bg-gradient-to-b from-transparent to-[#0f0f1a] pointer-events-none z-20" />
    </section>
  );
};

export default WhyRankly360;
