import React from 'react';
import { motion } from 'framer-motion';
import { Star, CheckCircle, TrendingUp, Shield, Target, ArrowLeft, FileText } from 'lucide-react';
import { Link as RouterLink } from 'react-router-dom';

const LocalCitations = () => {
  const features = [
    {
      icon: CheckCircle,
      title: '150x High DA Local Citations',
      description: 'Manually built citations on high Domain Authority local directories.'
    },
    {
      icon: Shield,
      title: '100% Safe & Manual',
      description: 'All citations are manually created with care based on extensive Local SEO experience.'
    },
    {
      icon: Target,
      title: 'NAP Consistency',
      description: '100% consistent Name, Address, Phone information across all platforms.'
    },
    {
      icon: TrendingUp,
      title: 'Proven Results',
      description: 'Tested strategy that delivers measurable ranking improvements.'
    },
    {
      icon: FileText,
      title: 'Full Reporting',
      description: 'Complete report with login access via Google Sheet for transparency.'
    }
  ];

  const requirements = [
    'Your/client website url',
    'Main target keyword & 10 supporting keywords',
    'NAP (business name, address, phone number)',
    'GBP link',
    'Short description of your company (110 characters)',
    'E-mail Address of your business'
  ];

  return (
    <section className="min-h-screen w-full bg-gradient-to-br from-primary-950 via-primary-900 to-black text-white flex flex-col items-center justify-center py-0 px-0">
      <div className="w-full max-w-5xl mx-auto flex flex-col items-center justify-center px-4 md:px-12 py-16">
        <div className="w-full bg-primary-900/90 rounded-3xl shadow-2xl p-8 md:p-16 mb-12 border border-primary-800">
          <h1 className="text-5xl md:text-6xl font-extrabold mb-6 text-electric-blue text-center drop-shadow-lg">Local Citations for Local SEO</h1>
          <p className="text-2xl text-gray-200 mb-10 text-center max-w-3xl mx-auto">Supercharge your online presence with 100% manually built, high-DA local citations—designed to boost your rankings on Google Maps and organic search.</p>
          <div className="flex flex-col md:flex-row gap-8 justify-center items-start mb-12">
            <div className="flex-1">
              <h2 className="text-3xl font-bold mb-4 text-orange-400">What's Included</h2>
              <ul className="space-y-4">
                {features.map((f, i) => (
                  <li key={i} className="flex flex-col gap-1 bg-primary-800/70 rounded-xl p-4 border-l-4 border-electric-blue">
                    <span className="font-semibold text-electric-blue text-lg">{f.title}</span>
                    <span className="text-gray-200 text-base">{f.description}</span>
                  </li>
                ))}
              </ul>
            </div>
          </div>
          <div className="mb-12">
            <h2 className="text-2xl font-bold mb-4 text-orange-400">What We Need From You</h2>
            <ul className="space-y-4">
              {requirements.map((r, i) => (
                <li key={i} className="flex items-center gap-3 bg-primary-800/70 rounded-xl p-4 border-l-4 border-orange-400">
                  <span className="text-electric-blue font-semibold text-lg">{r}</span>
                </li>
              ))}
            </ul>
          </div>
          <div className="flex flex-col items-center gap-6 mt-8">
            <button
              className="bg-gradient-to-r from-orange-500 to-electric-blue text-white px-12 py-5 rounded-2xl font-bold text-2xl shadow-lg hover:scale-105 hover:shadow-2xl transition-all duration-300"
            >
              Get Started
            </button>
            <RouterLink
              to={-1}
              className="text-electric-blue underline text-lg hover:text-orange-400 transition-colors"
            >
              ← Back
            </RouterLink>
          </div>
        </div>
      </div>
    </section>
  );
};

export default LocalCitations;
