import React from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';

const HERO_BG_GRADIENT = 'bg-gradient-to-b from-[#0f0f1a] to-[#09090f]';
const HEADLINE = 'Dominate Google Maps. Win Local Leads.';
const SUBHEAD = 'Local SEO that gets you found. More calls, more customers, no contracts.';
const BADGE_TEXT = 'ðŸš€ New: GMB Audit AI Now Live';

const fadeIn = (delay = 0) => ({
  initial: { opacity: 0, y: 24 },
  animate: { opacity: 1, y: 0 },
  transition: { duration: 0.8, delay },
});

const twinkleAnimation = {
  animate: {
    opacity: [0.3, 1, 0.3],
    scale: [0.8, 1.2, 0.8],
  },
  transition: {
    duration: 2,
    repeat: Infinity,
    ease: "easeInOut"
  }
};

const sunRiseAnimation = {
  animate: {
    y: [20, 0],
    opacity: [0.7, 1],
  },
  transition: {
    duration: 3,
    ease: "easeOut"
  }
};

const Hero = () => {
  const navigate = useNavigate();

  return (
    <section
      id="hero"
      className={`relative min-h-screen flex items-center justify-center ${HERO_BG_GRADIENT} overflow-hidden px-4`}
      style={{ fontFamily: "'Inter', 'Poppins', system-ui, sans-serif" }}
    >
      {/* --- Enhanced Glowing Electric Blue Horizon & Effects --- */}
      <div className="absolute inset-0 flex items-end justify-center pointer-events-none select-none z-0">
        {/* --- Planet Sunrise Effect --- */}
        {/* Main planet disc (with shadow and atmosphere) */}
        <motion.div 
          {...sunRiseAnimation}
          className="absolute bottom-0 left-1/2 -translate-x-1/2 w-[420px] h-[420px] md:w-[700px] md:h-[700px] rounded-full bg-gradient-to-t from-[#3abef9] via-[#0f0f1a] to-transparent opacity-90 shadow-[0_40px_120px_40px_rgba(58,190,249,0.25)] border-4 border-[#00ffff]/30"
          style={{ boxShadow: '0 60px 180px 60px #3abef933, 0 0 0 40px #00ffff22' }}
        />
        {/* Planet atmosphere ring */}
        <div className="absolute bottom-[-30px] left-1/2 -translate-x-1/2 w-[600px] h-[80px] md:w-[1000px] md:h-[120px] rounded-full bg-gradient-to-t from-[#00ffff]/30 to-transparent opacity-60 blur-[30px]" />
        {/* Horizon shadow */}
        <div className="absolute bottom-0 left-1/2 -translate-x-1/2 w-[420px] h-[60px] md:w-[700px] md:h-[100px] rounded-b-full bg-black/60 blur-[20px]" />
        {/* Electric blue ring */}
        <div className="absolute bottom-0 left-1/2 -translate-x-1/2 w-[700px] h-[220px] md:w-[1100px] md:h-[320px] rounded-full bg-[#3abef9] opacity-40 blur-[100px]" />
        {/* Outer blue ring */}
        <div className="absolute bottom-0 left-1/2 -translate-x-1/2 w-[1000px] h-[380px] md:w-[1400px] md:h-[480px] rounded-full bg-[#007bff] opacity-20 blur-[140px]" />
        {/* Additional atmospheric glow */}
        <div className="absolute bottom-0 left-1/2 -translate-x-1/2 w-[1200px] h-[450px] md:w-[1600px] md:h-[550px] rounded-full bg-[#00ffff] opacity-10 blur-[160px]" />
        {/* Enhanced SVG Concentric Arcs */}
        <svg className="absolute bottom-0 left-1/2 -translate-x-1/2 w-[1000px] h-[450px] md:w-[1400px] md:h-[600px]" viewBox="0 0 1400 600" fill="none" xmlns="http://www.w3.org/2000/svg">
          <ellipse cx="700" cy="600" rx="600" ry="140" stroke="#3abef9" strokeOpacity="0.12" strokeWidth="2.5" />
          <ellipse cx="700" cy="600" rx="500" ry="110" stroke="#3abef9" strokeOpacity="0.15" strokeWidth="2" />
          <ellipse cx="700" cy="600" rx="400" ry="80" stroke="#3abef9" strokeOpacity="0.18" strokeWidth="1.5" />
          <ellipse cx="700" cy="600" rx="300" ry="50" stroke="#00ffff" strokeOpacity="0.10" strokeWidth="1" />
          <ellipse cx="700" cy="600" rx="200" ry="30" stroke="#00ffff" strokeOpacity="0.08" strokeWidth="0.8" />
        </svg>
        {/* Enhanced Star Field with Twinkling */}
        <div className="absolute inset-0">
          {/* Original stars with twinkling */}
          <motion.div 
            {...twinkleAnimation}
            transition={{ ...twinkleAnimation.transition, delay: 0 }}
            className="absolute left-[15%] top-[20%] w-1 h-1 bg-white/70 rounded-full blur-[1px]" 
          />
          <motion.div 
            {...twinkleAnimation}
            transition={{ ...twinkleAnimation.transition, delay: 0.5 }}
            className="absolute left-[25%] top-[15%] w-1.5 h-1.5 bg-[#3abef9]/90 rounded-full blur-[1.5px]" 
          />
          <motion.div 
            {...twinkleAnimation}
            transition={{ ...twinkleAnimation.transition, delay: 1 }}
            className="absolute left-[35%] top-[25%] w-0.5 h-0.5 bg-white/50 rounded-full blur-[0.8px]" 
          />
          <motion.div 
            {...twinkleAnimation}
            transition={{ ...twinkleAnimation.transition, delay: 1.5 }}
            className="absolute left-[45%] top-[10%] w-1 h-1 bg-white/40 rounded-full blur-[1px]" 
          />

          {/* Additional stars */}
          <motion.div 
            {...twinkleAnimation}
            transition={{ ...twinkleAnimation.transition, delay: 0.3 }}
            className="absolute left-[65%] top-[18%] w-1.2 h-1.2 bg-[#00ffff]/80 rounded-full blur-[1.2px]" 
          />
          <motion.div 
            {...twinkleAnimation}
            transition={{ ...twinkleAnimation.transition, delay: 0.8 }}
            className="absolute left-[75%] top-[22%] w-0.8 h-0.8 bg-white/60 rounded-full blur-[1px]" 
          />
          <motion.div 
            {...twinkleAnimation}
            transition={{ ...twinkleAnimation.transition, delay: 1.2 }}
            className="absolute left-[85%] top-[12%] w-1.3 h-1.3 bg-[#3abef9]/70 rounded-full blur-[1.3px]" 
          />
          <motion.div 
            {...twinkleAnimation}
            transition={{ ...twinkleAnimation.transition, delay: 0.2 }}
            className="absolute left-[10%] top-[35%] w-0.6 h-0.6 bg-white/45 rounded-full blur-[0.8px]" 
          />
          <motion.div 
            {...twinkleAnimation}
            transition={{ ...twinkleAnimation.transition, delay: 0.7 }}
            className="absolute left-[20%] top-[40%] w-1.1 h-1.1 bg-[#00ffff]/65 rounded-full blur-[1.1px]" 
          />
          <motion.div 
            {...twinkleAnimation}
            transition={{ ...twinkleAnimation.transition, delay: 1.1 }}
            className="absolute left-[30%] top-[45%] w-0.9 h-0.9 bg-white/55 rounded-full blur-[1px]" 
          />

          {/* Stars near horizon */}
          <motion.div 
            {...twinkleAnimation}
            transition={{ ...twinkleAnimation.transition, delay: 0.4 }}
            className="absolute left-[40%] top-[60%] w-0.7 h-0.7 bg-[#3abef9]/50 rounded-full blur-[0.9px]" 
          />
          <motion.div 
            {...twinkleAnimation}
            transition={{ ...twinkleAnimation.transition, delay: 0.9 }}
            className="absolute left-[60%] top-[65%] w-1 h-1 bg-white/35 rounded-full blur-[1px]" 
          />
          <motion.div 
            {...twinkleAnimation}
            transition={{ ...twinkleAnimation.transition, delay: 1.4 }}
            className="absolute left-[80%] top-[55%] w-0.8 h-0.8 bg-[#00ffff]/40 rounded-full blur-[0.8px]" 
          />
        </div>

        {/* Floating particles */}
        <div className="absolute inset-0">
          <motion.div 
            animate={{ y: [-10, 10, -10], opacity: [0.3, 0.7, 0.3] }}
            transition={{ duration: 4, repeat: Infinity, ease: "easeInOut" }}
            className="absolute left-[12%] top-[30%] w-0.5 h-0.5 bg-[#3abef9]/30 rounded-full"
          />
          <motion.div 
            animate={{ y: [10, -10, 10], opacity: [0.4, 0.8, 0.4] }}
            transition={{ duration: 5, repeat: Infinity, ease: "easeInOut", delay: 1 }}
            className="absolute left-[70%] top-[25%] w-0.3 h-0.3 bg-white/25 rounded-full"
          />
          <motion.div 
            animate={{ y: [-5, 15, -5], opacity: [0.2, 0.6, 0.2] }}
            transition={{ duration: 6, repeat: Infinity, ease: "easeInOut", delay: 2 }}
            className="absolute left-[90%] top-[40%] w-0.4 h-0.4 bg-[#00ffff]/20 rounded-full"
          />
        </div>
      </div>

      {/* Content */}
      <div className="relative z-10 w-full max-w-3xl mx-auto flex flex-col items-center text-center">
        {/* Badge */}
        <motion.div
          {...fadeIn(0.1)}
          className="mb-6"
        >
          <span className="inline-block px-4 py-1.5 rounded-full bg-[#10111a] bg-opacity-90 text-[#3abef9] text-xs font-semibold tracking-wide shadow-sm border border-[#3abef9] border-opacity-60">
            {BADGE_TEXT}
          </span>
        </motion.div>

        {/* Headline */}
        <motion.h1
          {...fadeIn(0.2)}
          className="text-4xl md:text-6xl lg:text-7xl font-extrabold text-white mb-6 leading-tight tracking-tight"
          style={{ letterSpacing: '-0.03em' }}
        >
          {HEADLINE}
        </motion.h1>

        {/* Subheadline */}
        <motion.p
          {...fadeIn(0.35)}
          className="text-lg md:text-2xl text-[#9ca3af] mb-10 max-w-2xl mx-auto font-medium"
          style={{ letterSpacing: '-0.01em' }}
        >
          {SUBHEAD}
        </motion.p>

        {/* CTA Button */}
        <motion.div {...fadeIn(0.5)}>
          <button
            onClick={() => navigate('/packages')}
            className="px-8 py-4 rounded-full bg-gradient-to-r from-[#3abef9] to-[#007bff] text-white text-lg font-semibold shadow-xl transition-all duration-300 hover:shadow-[0_0_32px_0_rgba(58,190,249,0.5)] focus:outline-none focus:ring-2 focus:ring-[#3abef9] focus:ring-offset-2"
            style={{ fontFamily: "'Inter', 'Poppins', system-ui, sans-serif" }}
          >
            Get Started
          </button>
        </motion.div>
      </div>
    </section>
  );
};

export default Hero;
