import React from 'react';
import { motion } from 'framer-motion';
import { Check, Star, ArrowRight } from 'lucide-react';

const Pricing = () => {
  const plans = [
    {
      name: 'Starter',
      price: '$497',
      period: '/mo',
      description: 'Perfect for new local businesses getting started',
      features: [
        'GBP setup & optimization',
        '1 blog post per month',
        'Local citations setup',
        'Basic ranking reports',
        'Email support'
      ],
      cta: 'Start Now',
      popular: false
    },
    {
      name: 'Core',
      price: '$997',
      period: '/mo',
      description: 'Most Popular - Complete local SEO solution',
      features: [
        'Everything in Starter',
        'Map embed stacking',
        'Link building campaign',
        'Review generation system',
        'Monthly rankings reporting',
        'Priority support',
        'Competitor analysis'
      ],
      cta: 'Start Now',
      popular: true
    },
    {
      name: 'Dominate',
      price: '$1,997',
      period: '/mo',
      description: 'Ultimate local market domination',
      features: [
        'Everything in Core',
        'Review management',
        'Premium map authority',
        'Monthly consultation',
        'Custom strategy development',
        'Dedicated account manager',
        'Advanced analytics',
        'White-label reporting'
      ],
      cta: 'Start Now',
      popular: false
    }
  ];

  const addOns = [
    {
      name: 'Map PowerBoost',
      price: '$347',
      description: 'One-time Google Maps optimization boost',
      features: [
        '300 Local Geotagged Map Points',
        '15-45 mile radius coverage',
        '20 driving direction paths',
        '10 Geotagged images',
        'NAP embedded stacking',
        '99 High DA backlinks'
      ]
    },
    {
      name: 'Google Authority Embeds',
      price: '$347',
      description: 'One-time authority building package',
      features: [
        '800x iFrame Stacking Map Embeds',
        'Geotagged YouTube Video Embeds',
        'Web 2.0 Links from DA 20-30+ Sites',
        'High DA Do-Follow Backlinks',
        '30-Day Drip Feed',
        'Full Indexing Verification'
      ]
    }
  ];

  return (
    <section className="py-20 bg-gray-50">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        {/* Section Header */}
        <motion.div 
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl md:text-5xl font-bold text-primary-900 mb-6">
            Simple Pricing for Local Growth
          </h2>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            Choose the plan that fits your business goals. All plans include our 30-day results guarantee.
          </p>
        </motion.div>

        {/* Main Plans */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 max-w-7xl mx-auto mb-20">
          {plans.map((plan, index) => (
            <motion.div
              key={plan.name}
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: index * 0.1 }}
              viewport={{ once: true }}
              className={`relative bg-white rounded-2xl p-8 shadow-lg hover:shadow-xl transition-all duration-300 ${
                plan.popular ? 'ring-2 ring-orange-500 scale-105' : ''
              }`}
            >
              {/* Popular Badge */}
              {plan.popular && (
                <div className="absolute -top-4 left-1/2 transform -translate-x-1/2">
                  <div className="bg-gradient-to-r from-orange-500 to-orange-600 text-white px-6 py-2 rounded-full text-sm font-semibold flex items-center gap-2">
                    <Star className="w-4 h-4" />
                    Most Popular
                  </div>
                </div>
              )}

              {/* Plan Header */}
              <div className="text-center mb-8">
                <h3 className="text-2xl font-bold text-primary-900 mb-2">
                  {plan.name}
                </h3>
                <div className="mb-4">
                  <span className="text-4xl font-bold text-primary-900">{plan.price}</span>
                  <span className="text-gray-600">{plan.period}</span>
                </div>
                <p className="text-gray-600">{plan.description}</p>
              </div>

              {/* Features */}
              <ul className="space-y-4 mb-8">
                {plan.features.map((feature, featureIndex) => (
                  <li key={featureIndex} className="flex items-start gap-3">
                    <Check className="w-5 h-5 text-green-500 mt-0.5 flex-shrink-0" />
                    <span className="text-gray-700">{feature}</span>
                  </li>
                ))}
              </ul>

              {/* CTA */}
              <div className="space-y-3">
                <button className={`w-full py-3 px-6 rounded-xl font-semibold transition-all duration-300 ${
                  plan.popular
                    ? 'bg-orange-500 hover:bg-orange-600 text-white transform hover:scale-105'
                    : 'bg-primary-900 hover:bg-primary-800 text-white'
                }`}>
                  {plan.cta}
                </button>
                <button className="w-full py-2 px-6 text-primary-600 hover:text-primary-700 font-medium text-sm">
                  Learn More
                </button>
              </div>
            </motion.div>
          ))}
        </div>

        {/* Add-ons Section */}
        <motion.div 
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="max-w-4xl mx-auto"
        >
          <h3 className="text-3xl font-bold text-primary-900 text-center mb-12">
            One-Time Add-ons
          </h3>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            {addOns.map((addon, index) => (
              <motion.div
                key={addon.name}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
                className="bg-white rounded-2xl p-8 shadow-lg hover:shadow-xl transition-all duration-300 border border-gray-100"
              >
                <div className="text-center mb-6">
                  <h4 className="text-xl font-bold text-primary-900 mb-2">
                    {addon.name}
                  </h4>
                  <div className="text-3xl font-bold text-orange-500 mb-2">
                    {addon.price}
                  </div>
                  <p className="text-gray-600 text-sm">
                    {addon.description}
                  </p>
                </div>

                <ul className="space-y-3 mb-6">
                  {addon.features.map((feature, featureIndex) => (
                    <li key={featureIndex} className="flex items-start gap-3">
                      <Check className="w-4 h-4 text-green-500 mt-0.5 flex-shrink-0" />
                      <span className="text-gray-700 text-sm">{feature}</span>
                    </li>
                  ))}
                </ul>

                <div className="space-y-3">
                  <button className="w-full py-3 px-6 bg-orange-500 hover:bg-orange-600 text-white rounded-xl font-semibold transition-all duration-300">
                    Add to Cart
                  </button>
                  <button className="w-full py-2 px-6 text-orange-600 hover:text-orange-700 font-medium text-sm">
                    Learn More
                  </button>
                </div>
              </motion.div>
            ))}
          </div>
        </motion.div>

        {/* Guarantee Section */}
        <motion.div 
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="mt-20 text-center"
        >
          <div className="bg-gradient-to-r from-primary-900 to-primary-800 rounded-3xl p-8 md:p-12 text-white max-w-4xl mx-auto">
            <h3 className="text-3xl font-bold mb-4">
              30-Day Results Guarantee
            </h3>
            <p className="text-xl text-gray-200 mb-8">
              If you don't see improvements in your local rankings within 30 days, we'll work extra hard or give you a full refund. No questions asked.
            </p>
            <button className="bg-orange-500 hover:bg-orange-600 text-white px-8 py-4 rounded-xl font-semibold text-lg transition-all duration-300 transform hover:scale-105 flex items-center gap-2 mx-auto">
              Start Your Free Strategy Call
              <ArrowRight className="w-5 h-5" />
            </button>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default Pricing;
