import React from 'react';
import { motion } from 'framer-motion';
import { Star, ShieldCheck, Users, TrendingUp } from 'lucide-react';

const trustBadges = [
  { icon: <ShieldCheck className="w-6 h-6 text-blue-400" />, label: 'Google Partner' },
  { icon: <Users className="w-6 h-6 text-green-400" />, label: '500+ Businesses Helped' },
  { icon: <Star className="w-6 h-6 text-yellow-400" />, label: '4.9/5 Rating' },
  { icon: <TrendingUp className="w-6 h-6 text-pink-400" />, label: '98% Success Rate' },
];

const featuredLogos = [
  { src: '/public/images/logo.png', alt: 'Forbes' },
  { src: '/public/images/logo.png', alt: 'Moz' },
  { src: '/public/images/logo.png', alt: 'BrightLocal' },
  { src: '/public/images/logo.png', alt: 'Search Engine Land' },
];

const WhyRankly360 = () => {
  return (
    <section id="why-us" className="relative py-24 bg-gradient-to-b from-[#0f0f1a] to-[#09090f] overflow-hidden">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 flex flex-col md:flex-row items-center gap-12">
        {/* Left: Headline, subheadline, trust badges */}
        <div className="flex-1 min-w-[320px] max-w-xl">
          <motion.h2
            initial={{ opacity: 0, y: 40 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
            className="text-4xl md:text-5xl font-extrabold text-white mb-6 tracking-tight"
          >
            Why Top Local Businesses <span className="text-[#3abef9]">Choose Rankly360</span>
          </motion.h2>
          <motion.p
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
            viewport={{ once: true }}
            className="text-xl text-blue-100 mb-8 max-w-lg"
          >
            We don’t just promise results. We deliver them—backed by data, real client wins, and industry recognition.
          </motion.p>
          <div className="flex flex-wrap gap-4 mb-8">
            {trustBadges.map((badge, i) => (
              <motion.div
                key={badge.label}
                initial={{ opacity: 0, scale: 0.8 }}
                whileInView={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.5, delay: 0.3 + i * 0.1 }}
                viewport={{ once: true }}
                className="flex items-center gap-2 bg-[#181c2a]/80 border border-[#3abef9]/20 rounded-xl px-4 py-2 shadow-md backdrop-blur-md hover:scale-105 transition-transform cursor-pointer"
              >
                {badge.icon}
                <span className="text-white font-semibold text-base">{badge.label}</span>
              </motion.div>
            ))}
          </div>
          <div className="flex gap-4 mt-4">
            <button className="bg-gradient-to-r from-[#3ABEF9] to-[#007BFF] hover:from-[#007BFF] hover:to-[#0052CC] text-white font-semibold py-4 px-8 rounded-xl transition-all duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl">
              Get My Free Local SEO Plan
            </button>
            <button className="bg-transparent border-2 border-[#3ABEF9] text-white font-semibold py-4 px-8 rounded-xl transition-all duration-300 hover:bg-[#3ABEF9] hover:text-white">
              See Real Results
            </button>
          </div>
        </div>
        {/* Right: Testimonial card and credibility section */}
        <div className="flex-1 flex flex-col items-center min-w-[320px]">
          <motion.div
            initial={{ opacity: 0, y: 40 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
            viewport={{ once: true }}
            className="relative bg-white/10 backdrop-blur-lg border border-[#3abef9]/30 rounded-3xl shadow-2xl p-8 md:p-10 max-w-md w-full mb-8"
            style={{ boxShadow: '0 8px 32px 0 rgba(58,190,249,0.15)' }}
          >
            <div className="flex items-center gap-3 mb-2">
              <Star className="w-6 h-6 text-yellow-400" />
              <Star className="w-6 h-6 text-yellow-400" />
              <Star className="w-6 h-6 text-yellow-400" />
              <Star className="w-6 h-6 text-yellow-400" />
              <Star className="w-6 h-6 text-yellow-400" />
              <span className="text-white font-bold ml-2">4.9/5</span>
            </div>
            <p className="text-lg text-white font-medium mb-4 italic">
              “Rankly360 took us from invisible to #1 in Google Maps for our city. We saw a 300% increase in calls in just 2 months. The team is responsive, honest, and truly cares about our growth.”
            </p>
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 rounded-full bg-[#3abef9] flex items-center justify-center text-white font-bold text-xl">J</div>
              <div>
                <div className="text-white font-semibold">Jessica M.</div>
                <div className="text-blue-100 text-xs">Owner, City Dental Care</div>
              </div>
            </div>
          </motion.div>
          {/* Credibility section */}
          <div className="w-full flex flex-col items-center mt-4">
            <div className="text-blue-100 text-sm mb-2 font-semibold">Trusted by Local Businesses</div>
            <div className="flex flex-wrap gap-4 justify-center">
              <div className="flex items-center gap-2 bg-[#181c2a]/80 border border-[#3abef9]/20 rounded-xl px-4 py-2 shadow-md">
                <ShieldCheck className="w-5 h-5 text-blue-400" />
                <span className="text-white text-sm font-semibold">Google Partner</span>
              </div>
              <div className="flex items-center gap-2 bg-[#181c2a]/80 border border-[#3abef9]/20 rounded-xl px-4 py-2 shadow-md">
                <Users className="w-5 h-5 text-green-400" />
                <span className="text-white text-sm font-semibold">500+ Businesses Helped</span>
              </div>
              <div className="flex items-center gap-2 bg-[#181c2a]/80 border border-[#3abef9]/20 rounded-xl px-4 py-2 shadow-md">
                <Star className="w-5 h-5 text-yellow-400" />
                <span className="text-white text-sm font-semibold">4.9/5 Client Rating</span>
              </div>
              <div className="flex items-center gap-2 bg-[#181c2a]/80 border border-[#3abef9]/20 rounded-xl px-4 py-2 shadow-md">
                <TrendingUp className="w-5 h-5 text-pink-400" />
                <span className="text-white text-sm font-semibold">98% Success Rate</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default WhyRankly360;
