import React from 'react';
import { motion } from 'framer-motion';
import { Star, ShieldCheck, Users, TrendingUp } from 'lucide-react';
import { useState, useEffect } from 'react';

// Example pin data for the map
const mapPins = [
  { cx: 120, cy: 80, label: 'Dentist in Miami: +312% calls' },
  { cx: 60, cy: 60, label: 'Bakery in Austin: +220% GMB views' },
  { cx: 200, cy: 50, label: 'Law Firm in NYC: #1 in Google Maps' },
  { cx: 90, cy: 120, label: 'Plumber in Houston: #1 in Maps 3-Pack' },
  { cx: 170, cy: 100, label: 'Salon in Chicago: 4x more bookings' },
  { cx: 220, cy: 130, label: 'Chiropractor in Atlanta: +250% new leads' },
];

function AnimatedStatBar() {
  const [count, setCount] = useState(0);
  useEffect(() => {
    let start = 0;
    const end = 500;
    if (count < end) {
      const timer = setInterval(() => {
        setCount((prev) => {
          if (prev < end) return prev + 5;
          clearInterval(timer);
          return end;
        });
      }, 10);
      return () => clearInterval(timer);
    }
  }, [count]);
  return (
    <div className="w-full max-w-md mx-auto mt-8 mb-2">
      <div className="text-center text-lg text-blue-100 mb-2 font-semibold">Over <span className="text-[#3abef9] text-2xl font-bold">{count}+</span> Local Businesses Helped</div>
      <div className="relative h-4 bg-[#181c2a] rounded-full overflow-hidden shadow-inner">
        <div className="absolute left-0 top-0 h-full bg-gradient-to-r from-[#3abef9] to-[#00ffff] rounded-full shadow-lg" style={{ width: `${Math.min(count / 5, 100)}%` }} />
      </div>
    </div>
  );
}

function ImpactMap() {
  const [hovered, setHovered] = useState(null);
  return (
    <div className="flex flex-col items-center w-full">
      <div className="relative w-[320px] h-[180px] md:w-[480px] md:h-[270px] mx-auto mb-6">
        {/* Stylized US map SVG (abstract, not geographically accurate) */}
        <svg viewBox="0 0 260 150" className="w-full h-full drop-shadow-2xl">
          <ellipse cx="130" cy="75" rx="120" ry="60" fill="#23263a" stroke="#3abef9" strokeWidth="2" filter="url(#glow)" />
          <defs>
            <filter id="glow" x="-50%" y="-50%" width="200%" height="200%">
              <feGaussianBlur stdDeviation="6" result="coloredBlur" />
              <feMerge>
                <feMergeNode in="coloredBlur" />
                <feMergeNode in="SourceGraphic" />
              </feMerge>
            </filter>
          </defs>
          {mapPins.map((pin, i) => (
            <g key={i} onMouseEnter={() => setHovered(i)} onMouseLeave={() => setHovered(null)}>
              <circle cx={pin.cx} cy={pin.cy} r="7" fill="#3abef9" className="animate-pulse" />
              <circle cx={pin.cx} cy={pin.cy} r="3" fill="#fff" />
              {hovered === i && (
                <foreignObject x={pin.cx - 60} y={pin.cy - 40} width="120" height="32">
                  <div className="bg-[#23263a] border border-[#3abef9]/40 text-white text-xs rounded-lg px-3 py-2 shadow-lg animate-fadeIn" style={{ pointerEvents: 'none' }}>
                    {pin.label}
                  </div>
                </foreignObject>
              )}
            </g>
          ))}
        </svg>
      </div>
    </div>
  );
}

const trustBadges = [
  { icon: <ShieldCheck className="w-6 h-6 text-blue-400" />, label: 'Google Partner' },
  { icon: <Users className="w-6 h-6 text-green-400" />, label: '500+ Businesses Helped' },
  { icon: <Star className="w-6 h-6 text-yellow-400" />, label: '4.9/5 Rating' },
  { icon: <TrendingUp className="w-6 h-6 text-pink-400" />, label: '98% Success Rate' },
];

const tickerResults = [
  'Bakery in Austin: +220% GMB views',
  'Law Firm in NYC: #1 in Google Maps',
  'Dentist in Miami: +312% calls',
  'Plumber in London: #1 in Maps 3-Pack',
  'Salon in LA: 4x more bookings',
  'Landscaper in Chicago: +180% website visits',
  'Chiropractor in Toronto: +250% new leads',
  'Restaurant in Dallas: #1 for "best tacos"',
];

function LiveResultsTicker() {
