import React from 'react';
import { motion } from 'framer-motion';

const HERO_BG_GRADIENT = 'bg-gradient-to-b from-[#0f0f1a] to-[#09090f]';
const HEADLINE = 'Dominate Google Maps. Win Local Leads.';
const SUBHEAD = 'Local SEO that gets you found. More calls, more customers, no contracts.';
const BADGE_TEXT = 'ðŸš€ New: GMB Audit AI Now Live';

const fadeIn = (delay = 0) => ({
  initial: { opacity: 0, y: 24 },
  animate: { opacity: 1, y: 0 },
  transition: { duration: 0.8, delay },
});

const Hero = () => {
  return (
    <section
      id="hero"
      className={`relative min-h-screen flex items-center justify-center ${HERO_BG_GRADIENT} overflow-hidden px-4`}
      style={{ fontFamily: "'Inter', 'Poppins', system-ui, sans-serif" }}
    >
      {/* --- Glowing Electric Blue Horizon & Effects --- */}
      <div className="absolute inset-0 flex items-end justify-center pointer-events-none select-none z-0">
        {/* Main white/blue core glow */}
        <div className="absolute bottom-0 left-1/2 -translate-x-1/2 w-[420px] h-[120px] md:w-[700px] md:h-[200px] rounded-full bg-white opacity-60 blur-[60px]" />
        {/* Electric blue ring */}
        <div className="absolute bottom-0 left-1/2 -translate-x-1/2 w-[600px] h-[180px] md:w-[900px] md:h-[260px] rounded-full bg-[#3abef9] opacity-40 blur-[80px]" />
        {/* Outer blue ring */}
        <div className="absolute bottom-0 left-1/2 -translate-x-1/2 w-[900px] h-[320px] md:w-[1200px] md:h-[400px] rounded-full bg-[#007bff] opacity-20 blur-[120px]" />
        {/* SVG Concentric Arcs */}
        <svg className="absolute bottom-0 left-1/2 -translate-x-1/2 w-[900px] h-[400px] md:w-[1200px] md:h-[500px]" viewBox="0 0 1200 500" fill="none" xmlns="http://www.w3.org/2000/svg">
          <ellipse cx="600" cy="500" rx="500" ry="120" stroke="#3abef9" strokeOpacity="0.08" strokeWidth="2" />
          <ellipse cx="600" cy="500" rx="400" ry="90" stroke="#3abef9" strokeOpacity="0.10" strokeWidth="1.5" />
          <ellipse cx="600" cy="500" rx="300" ry="60" stroke="#3abef9" strokeOpacity="0.13" strokeWidth="1" />
        </svg>
        {/* Star dots */}
        <div className="absolute bottom-[120px] left-1/2 -translate-x-1/2">
          <div className="absolute left-[-120px] top-[-30px] w-1 h-1 bg-white/60 rounded-full blur-[1.5px]" />
          <div className="absolute left-[80px] top-[-10px] w-1.5 h-1.5 bg-[#3abef9]/80 rounded-full blur-[1.5px]" />
          <div className="absolute left-[-60px] top-[30px] w-0.5 h-0.5 bg-white/40 rounded-full blur-[1px]" />
          <div className="absolute left-[140px] top-[40px] w-1 h-1 bg-white/30 rounded-full blur-[1.5px]" />
        </div>
      </div>

      {/* Content */}
      <div className="relative z-10 w-full max-w-3xl mx-auto flex flex-col items-center text-center">
        {/* Badge */}
        <motion.div
          {...fadeIn(0.1)}
          className="mb-6"
        >
          <span className="inline-block px-4 py-1.5 rounded-full bg-[#10111a] bg-opacity-90 text-[#3abef9] text-xs font-semibold tracking-wide shadow-sm border border-[#3abef9] border-opacity-60">
            {BADGE_TEXT}
          </span>
        </motion.div>

        {/* Headline */}
        <motion.h1
          {...fadeIn(0.2)}
          className="text-4xl md:text-6xl lg:text-7xl font-extrabold text-white mb-6 leading-tight tracking-tight"
          style={{ letterSpacing: '-0.03em' }}
        >
          {HEADLINE}
        </motion.h1>

        {/* Subheadline */}
        <motion.p
          {...fadeIn(0.35)}
          className="text-lg md:text-2xl text-[#9ca3af] mb-10 max-w-2xl mx-auto font-medium"
          style={{ letterSpacing: '-0.01em' }}
        >
          {SUBHEAD}
        </motion.p>

        {/* CTA Button */}
        <motion.div {...fadeIn(0.5)}>
          <button
            className="px-8 py-4 rounded-full bg-gradient-to-r from-[#3abef9] to-[#007bff] text-white text-lg font-semibold shadow-xl transition-all duration-300 hover:shadow-[0_0_32px_0_rgba(58,190,249,0.5)] focus:outline-none focus:ring-2 focus:ring-[#3abef9] focus:ring-offset-2"
            style={{ fontFamily: "'Inter', 'Poppins', system-ui, sans-serif" }}
          >
            Get Started
          </button>
        </motion.div>
      </div>
    </section>
  );
};

export default Hero;
