import React, { useState } from 'react';
import { MessageCircle, X, Send } from 'lucide-react';

const ChatWidget = () => {
  const [open, setOpen] = useState(false);
  const [message, setMessage] = useState('');
  const [submitted, setSubmitted] = useState(false);

  const handleSend = (e) => {
    e.preventDefault();
    if (message.trim()) {
      setSubmitted(true);
      console.log('Chat message:', message);
      setMessage('');
    }
  };

  return (
    <div className="fixed bottom-6 right-6 z-[9999]">
      {/* Floating Bubble */}
      {!open && (
        <button
          className="bg-[#0F172A] hover:bg-[#1e3350] text-white rounded-full shadow-lg w-16 h-16 flex items-center justify-center transition-all duration-300 border-4 border-orange-500 animate-bounce"
          onClick={() => setOpen(true)}
          aria-label="Open chat"
        >
          <MessageCircle className="w-8 h-8 text-orange-500" />
        </button>
      )}
      {/* Chat Panel */}
      {open && (
        <div className="w-80 max-w-[95vw] bg-[#0F172A] text-white rounded-2xl shadow-2xl p-4 flex flex-col gap-4 animate-fadeInUp border-2 border-orange-500 relative">
          <button
            className="absolute top-2 right-2 text-orange-500 hover:text-orange-400"
            onClick={() => { setOpen(false); setSubmitted(false); }}
            aria-label="Close chat"
          >
            <X className="w-6 h-6" />
          </button>
          <div className="flex items-center gap-2 mb-2">
            <MessageCircle className="w-6 h-6 text-orange-500" />
            <span className="font-bold text-lg">Rankly360 Chat</span>
          </div>
          {!submitted ? (
            <form onSubmit={handleSend} className="flex flex-col gap-3">
              <div className="text-sm mb-1">ðŸ‘‹ Need help ranking on Google Maps?</div>
              <textarea
                className="rounded-lg p-2 text-gray-900 min-h-[60px] resize-none focus:outline-none focus:ring-2 focus:ring-orange-500"
                placeholder="Type your message..."
                value={message}
                onChange={e => setMessage(e.target.value)}
                required
              />
              <button
                type="submit"
                className="flex items-center justify-center gap-2 bg-orange-500 hover:bg-orange-600 text-white font-semibold py-2 px-4 rounded-lg transition-all duration-200 shadow-md"
              >
                <Send className="w-4 h-4" /> Send Message
              </button>
            </form>
          ) : (
            <div className="flex flex-col items-center gap-2 py-8">
              <div className="text-3xl">âœ…</div>
              <div className="font-semibold text-lg text-center">Thanks! We'll get back to you shortly.</div>
            </div>
          )}
        </div>
      )}
      <style>{`
        @media (max-width: 640px) {
          .fixed.bottom-6.right-6 { bottom: 1rem; right: 1rem; }
          .w-80 { width: 95vw !important; }
        }
        .animate-fadeInUp {
          animation: fadeInUp 0.3s cubic-bezier(.39,.575,.565,1) both;
        }
        @keyframes fadeInUp {
          0% { opacity: 0; transform: translateY(40px); }
          100% { opacity: 1; transform: translateY(0); }
        }
      `}</style>
    </div>
  );
};

export default ChatWidget;
